import display7DayData from '../Helpers.js';
import { useEffect, useState } from 'react';

const HourlyDisplay = () => {

    const [allData, setData] = useState([]);

    let getLoc = JSON.parse(localStorage.getItem("location"));
    if (!getLoc) {
        getLoc = "New York, USA";
    }

    useEffect(() => {

        const getData = async (place) => {
            let getit = await display7DayData(place);
            setData(getit[0]);
        }
        getData(getLoc);
    }, []);

    return (
        <div className="hidden" id="hourlyDisplay">
            {allData.map((hr, idx) => {
                return (
                    <div className="eachHour" key={idx}>
                        <p className="hourText">{hr[0]}:00</p>
                    </div>
                )
            })}
        </div>
    );
};

export default HourlyDisplay;
