import { BrowserRouter as Router, Routes, Route } from "react-router-dom"
import "bootstrap/dist/css/bootstrap.min.css"

// pages & component
import Home from "./pages/Home";
import AccountSettings from "./pages/AccountSettings";
import ErrorPage from "./pages/ErrorPage";
import Login from "./pages/Login";

let getLoc = JSON.parse(localStorage.getItem("location"));
if (!getLoc) {
    getLoc = "New York, USA";
}

useEffect(() => {

    const getData = async (place) => {
        let hoursRainProb = [];
        let getit = await getHourly_Weekly_CurrentWeather(place);
        for (let i = 0; i < getit[0].length; i++) {
            if (hoursRainProb.length === 6) {
                break;
            } else if (i % 2 === 0) {
                let temp = [getit[0][i][0], getit[0][i][4]];
                hoursRainProb.push(temp);
            }
        }
        window.hoursProbPercent = hoursRainProb;
    }
    getData(getLoc);
}, []);

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} ></Route>
        <Route path="/accountsettings" element={<AccountSettings />} ></Route>
        <Route path="/login" element={<Login />}></Route>
        <Route path="*" element={<ErrorPage />} ></Route>
      </Routes>
    </Router>
  );
}

export default App;
